import movies from '../database/movies';

const resolvers = {
  Query: {
    movies: () => movies,
    movie: (_, { id }) => {
      return movies.filter(movie => movie.id === id)[0]
    }
  },
  Mutation: {
    addMovie: (_, { name, rating }) => {
      // ì˜í™” ì œëª© ì¤‘ë³µ ê²€ì‚¬
      if (movies.find(movie => movie.name === name)) return null;
      
      // ë°ì´í„°ë² ì´ìŠ¤ì— ì¶”ê°€
      const newMovie = {
        id: movies.length + 1,
        name,
        rating
      };
      movies.push(newMovie);
      return newMovie;
    }
  }
};

export default resolvers;

